import { useBeatsData } from './BeatsDataContext.js'
import React, { useState, useRef, useEffect } from 'react';


const Player = () => {
    var beats = useBeatsData();
    const audioRef = useRef(null);
    const coverRef = useRef(null)
    function cheapestLicense(licenses) {
        try {
            let cheapestPrice = Infinity;
            let cheapestCurrency = '';
            for (const license of licenses) {
                const numericPrice = parseFloat(license.price);
                if (numericPrice < cheapestPrice) {
                    cheapestPrice = numericPrice;
                    cheapestCurrency = license.currency;
                }
            }
            if (cheapestCurrency === '$') {
                return `${cheapestCurrency}${cheapestPrice.toFixed(2)}`;
            }
            else {
                return `${cheapestPrice.toFixed(2)}${cheapestCurrency}`;
            }
        } catch (error) {
            return '0.00€'
        }
    }


    const [isPlaying, setIsPlaying] = useState(false);
    const handlePlayPause = () => {
        if (isPlaying) {
            audioRef.current.pause();
        } else {
            audioRef.current.play();
        }
        setIsPlaying(!isPlaying);
    };

    const [currentBeat, setCurrentBeat] = useState({
        index: 0,
        title: beats[0]?.title || 'Default',
        price: cheapestLicense(beats[0]?.licenses),
        artwork: beats[0]?.artwork_thumbnail || 'Default',
    });
    const selectBeat = (beatIndex) => {
        const selectedBeat = beats[beatIndex];
        setCurrentBeat({
            index: beatIndex,
            title: selectedBeat.title,
            price: cheapestLicense(selectedBeat.licenses),
            artwork: selectedBeat.artwork,
        });
    };
    const handleBeatSwitch = (addend) => {
        setCurrentBeat((prevBeat) => {
            const newBeatIndex = (prevBeat.index + addend + beats.length) % beats.length;
            const newBeat = beats[newBeatIndex];

            return {
                index: newBeatIndex,
                title: newBeat.title,
                mp3: newBeat.mp3_file,
                price: cheapestLicense(newBeat.licenses),
                artwortk: newBeat.artwork_thumbnail,
            };
        });
    };
    useEffect(() => {
        if (audioRef.current) {
            audioRef.current.src = beats[currentBeat.index]?.mp3_file || '';
            audioRef.current.play();
            setIsPlaying(true)
        }
        if (coverRef.current) {
            coverRef.current.src = currentBeat?.artwork;
        }
    }, [currentBeat]);
    const [isMuted, setIsMuted] = useState(false)
    const handleMute = () => {
        if (isMuted) {
            audioRef.current.muted = false;
        } else {
            audioRef.current.muted = true;
        }
        setIsMuted(!isMuted)
    }
    if (!beats[0]) {
        return <p>loading...</p>
    }
    return(
        <div className="fixed inset-x-0 bottom-0">
            <audio ref={audioRef} controlsList="nodownload" controls>
                {beats?.map((beat, index) => (<source key={beat.id} src={beat.mp3_file} type="audio/mpeg" />))}
            </audio>
            <div className="bg-gray w-full h-[6px]"></div>
            <div className="bg-blue h-16 flex items-center justify-between">

                <div className="lg:col-span-5 col-span-9 p-0 flex items-center">
                    <img src={currentBeat.artwork} ref={coverRef} alt="" className="w-16 h-16 hidden sm:flex"></img>
                    <div className="pl-2">
                        <span className="font-semibold text-white block">{currentBeat.title}</span>
                        <span className="font-thin text-gray text-sm block">Hadin</span>
                    </div>
                </div>

                <div className="flex items-center">
                    <button className="mx-2 hidden md:flex">
                        <img src="./static/repeat-1.svg" alt="" className="w-3"></img>
                    </button>
                    <button className="mx-2" onClick={() => handleBeatSwitch(-1)}>
                        <img src="./static/back.svg" alt="" className="w-5"></img>
                    </button>
                    <button className="mx-2" onClick={handlePlayPause}>
                        <img src={isPlaying ? './static/pause-white.svg' : './static/play-white.svg'}

                            alt="" className="w-10"></img>
                    </button>
                    <button className="mx-2" onClick={() => handleBeatSwitch(1)}>
                        <img src="./static/next.svg" alt="" className="w-5"></img>
                    </button>
                    <button className="mx-2 hidden md:flex">
                        <img src="./static/shuffle.svg" alt="" className="w-3"></img>
                    </button>
                </div>

                <div className="hidden md:flex pr-4">

                    <button className="mr-8" onClick={handleMute}>
                        <img src={isMuted ? './static/volume-mute.svg' : './static/volume-down.svg'} alt="" className="w-7"></img>
                    </button>

                    <button className="px-3 py-1.5 text-black bg-white text-sm rounded-lg font-semibold flex items-center">
                        <img src="static/bag-fill-black.svg" alt=""></img>
                        <span className="pl-2">{currentBeat.price}</span>
                    </button>

                </div>
            </div>
        </div>
    )

}

export default Player
