import './App.css';
import Header from './components/nav.js'
import Spotlight from './components/spotlight.js'
import SearchBar from './components/searchbar.js'
import Catalog from './components/catalog.js'
import Player from './components/player.js'
//import { DataProvider } from './components/BeatsDataContext.js'
import React, { useState, useEffect } from 'react';



function App() {
  const [beats, setBeats] = useState([]);
  const [loading, setLoading] = useState(true);
  const getBeats = async () => {
    try {
      let response = await fetch('http://127.0.0.1:8000/api/beats/');
      let data = await response.json();
      setBeats(data);
    } catch (error) {
      console.error('Error fetching beats data:', error);
    } finally {
      setLoading(false);
    }
  };
  useEffect(() => {
    getBeats();
  }, []);
  const [currentBeat, setCurrentBeat] = useState({
    index: 0,
    title: 'Default',
    price: '0.00€',
    artwork: 'Default',
  });
  const [Spotlight, s] = useState({
    index: 0,
    title: 'Default',
    price: '0.00€',
    artwork: 'Default',
  });

  function cheapestLicense(licenses) {
    try {
      let cheapestPrice = Infinity;
      let cheapestCurrency = '';
      for (const license of licenses) {
        const numericPrice = parseFloat(license.price);
        if (numericPrice < cheapestPrice) {
          cheapestPrice = numericPrice;
          cheapestCurrency = license.currency;
        }
      }
      if (cheapestCurrency === '$') {
        return `${cheapestCurrency}${cheapestPrice.toFixed(2)}`;
      }
      else {
        return `${cheapestPrice.toFixed(2)}${cheapestCurrency}`;
      }
    } catch (error) {
      return '0.00€'
    }
  }
  return (
    <div className="App min-h-screen">
      <Header />
      <Spotlight currentBeat={currentBeat} />
      <SearchBar setBeats={setBeats} />
      <Catalog beats={beats} setCurrentBeat={setCurrentBeat} cheapestLicense={cheapestLicense} />
      <Player beats={beats} setCurrentBeat={setCurrentBeat} currentBeat={currentBeat} cheapestLicense={cheapestLicense} />
    </div>
  );
}

export default App;
