import React, { useState, useEffect } from 'react'

const Catalog = () =>{

    let [beats, setBeats] = useState([])
    useEffect(()=>{
      getBeats()
    },[])

    let getBeats = async () =>{
      let response = await fetch('http://127.0.0.1:8000/api/beats/')
      let data =  await response.json()
      console.log(data)
      setBeats(data)
    }

    function cheapest_license(licenses){
      let cheapestPrice = Infinity;
      let cheapestCurrency = '';
      for (const license of licenses) {
        const numericPrice = parseFloat(license.price);
        if (numericPrice < cheapestPrice) {
            cheapestPrice = numericPrice;
            cheapestCurrency = license.currency;
        }
      }
      if (cheapestCurrency == '$'){
        return `${cheapestCurrency}${cheapestPrice.toFixed(2)}`;
      }
      else{
        return `${cheapestPrice.toFixed(2)}${cheapestCurrency}`;
      }
    }

    return (
        <div className="pb-[200px]">
        <div className="grid-cols-12 font-bold text-sm gap-1 px-32 hidden lg:grid">
          <div className="col-span-5 pr-2 pb-2 pt-2 pl-[82px]">TITLE</div>
          <div className="col-span-1 p-2">TIME</div>
          <div className="col-span-1 p-2">BPM</div>
          <div className="col-span-3 p-2">TAGS</div>
          <div className="col-span-2 p-2"></div>
        </div>

        {beats?.map((beat,index) => (
          <div className="grid grid-cols-12 gap-1 px-1 lg:px-32 cursor-pointer">
          <div className="lg:col-span-5 col-span-9 p-2 flex items-center">
           <img src={beat.artwork_thumbnail} alt="error" className="w-[50px] h-[50px] rounded-lg"></img>
           <span className="lg:pl-6 pl-4 font-semibold">{beat.title}</span>
          </div>
          <div className="col-span-1 p-2 hidden lg:flex items-center ">{beat.duration.slice(3)}</div>
          <div className="col-span-1 p-2 hidden lg:flex items-center ">{beat.bpm}</div>
          <div className="col-span-3 p-2  hidden lg:flex items-center">{beat.tags}</div>
          <div className="lg:col-span-2 col-span-3 pr-2 flex items-center  place-content-end">
              <button className="hidden md:flex text-black bg-white p-2 mr-3 border-solid border-2 border-gray drop-shadow-sm text-sm rounded-lg font-semibold flex items-center">
                <img src="static/share-fill.svg" alt=""></img>
              </button>
              <button className="px-3 py-2 flex items-center text-white bg-blue text-sm rounded-lg font-semibold flex">
                  <img src="static/bag-plus-fill.svg" alt=""></img>
                  <span className="hidden sm:flex pl-2">{cheapest_license(beat.licenses)}</span>
              </button>
          </div>
        </div>

        ))}

        </div>
    )
}

export default Catalog
