import React from 'react';
import { useLocation } from 'react-router-dom';

function Checkout() {
 const location = useLocation();
 const cart = location.state?.cart || { items: [] }; // Default to empty cart if state isn't passed

 return (
  <div>
   <h1>Checkout</h1>
   <p>Cart Items: {cart.items.length}</p>
   {/* Render your checkout form here */}
  </div>
 );
}

export default Checkout;
