import React from 'react';
import { useLocation } from 'react-router-dom';

function Checkout() {
 const location = useLocation();
 const cart = location.state?.cart || { items: [] };

 function tags2title(tags) {
  if (tags) {
   return tags.replace(/,/g, ' x')
  }
  return ''
 }

 return (
  <div className="App min-h-screen max-w-screen-2xl mx-auto">
   <nav className="bg-blue top-0 z-50 flex flex-row h-16 py-4 px-2 lg:px-8">
    <img src="static/logo_white.png" className="lg:h-full  h-5/6 pt-2 lg:pt-0 flex-initial align-middle justify-self-start" alt=""></img>
   </nav>
   <div className="grid grid-cols-12 pt-12 p-2 md:p-12">

    <div className="w-100 md:col-span-7 col-span-12">
     <div className="w-100 flex justify-between mb-8">
      <span class="text-2xl font-semibold">Checkout ({cart.items.length})</span>
     </div>

     {cart.items?.map((item, index) => (
      <div key={index} className="grid grid-cols-12 gap-1 px-1 ">
       {/* Beat Details */}
       <div className="col-span-10 p-2 cursor-pointer flex">
         <img src={item.beat.artwork_thumbnail} alt="error" className="w-[50px] h-[50px] rounded-lg" />
         <div>
         <span className="lg:pl-6 pl-4 font-semibold">
          {tags2title(item.beat.tags)} Type Beat - {item.beat.title}
         </span>
         <p className="text-sm lg:pl-6 pl-4">License: {item.license.license_type}</p>
         </div>
       </div>

       <div className="col-span-2 flex bg flex-col justify-center h-full">
        <p className="">{item.license.price} {item.license.currency}</p>
       </div>
      </div>
     ))}

    </div>

    <div className="md:col-span-5 col-span-12 f bg-blue rounded">
     bla
    </div>

   </div>
  </div >
 );
}

export default Checkout;
