import React from 'react';
import { useLocation } from 'react-router-dom';

function Checkout() {
 const location = useLocation();
 const cart = location.state?.cart || { items: [] };

 return (
  <div className="App min-h-screen max-w-screen-2xl mx-auto">
   <nav className="bg-blue top-0 z-50 flex flex-row h-16 py-4 px-2 lg:px-8">
    <img src="static/logo_white.png" className="lg:h-full  h-5/6 pt-2 lg:pt-0 flex-initial align-middle justify-self-start" alt=""></img>
   </nav>
   <div className="grid grid-cols-12 pt-12 p-2 md:p-12">

    <div className="w-100 md:col-span-7 col-span-12">
     <div className="w-100 flex justify-between">
      <span class="text-2xl font-semibold">Checkout</span>
      <p>Cart Items: {cart.items.length}</p>
     </div>
     {cart.items?.map((beat, index) => (
      <div className="grid grid-cols-12 gap-1 px-1 lg:px-32">
       <div className="md:col-span-9 col-s p-2 flex items-center cursor-pointer">
        <img src={beat.artwork_thumbnail} alt="error" className="w-[50px] h-[50px] rounded-lg"></img>
        <span className="lg:pl-6 pl-4 font-semibold">{beat.title}</span>
       </div>

      </div>

     ))}
    </div>

    <div className="md:col-span-5 col-span-12 f bg-blue rounded">

    </div>

   </div>
  </div>
 );
}

export default Checkout;
