import React, { useState, useEffect } from 'react';


const Dialog = (props) => {
  const setDialogOpen = props.setDialogOpen;
  const dialogRef = props.dialogRef;

  const [isUseTermsDisplayed, setUseTermsDisplay] = useState(false);
  useEffect(() => {
    if (isUseTermsDisplayed) {
      document.getElementById('dialogChevron').style.transform = "rotate(180deg)"
    }
    else {
      document.getElementById('dialogChevron').style.transform = "intial"
    }
  }, [isUseTermsDisplayed])

  return (
    <dialog ref={dialogRef} className="lg:w-2/3 w-full p-10 rounded ">
      <div className="w-100 flex justify-between">
        <span className="text-2xl font-semibold">Choose License</span>
        <button onClick={() => setDialogOpen(false)}
          className="bg-gray py-1 px-2 rounded"
        >Close</button>
      </div>
      <div className="grid grid-cols-3 gap-4 w-100 pt-10">
        <div className="p-3 pb-6 rounded bg-gray cursor-pointer leading-loose">
          <div className="text-lg font-semibold">Basic</div>
          <div className="text-base">24.99€</div>
          <div className="text-xs">mp3</div>
        </div>
        <div className="p-3 pb-6 rounded bg-gray  cursor-pointer leading-loose">
          <div className="text-lg font-semibold">Premium</div>
          <div className="text-base">39.99€</div>
          <div className="text-xs">mp3, wav</div>
        </div>
        <div className="p-3 pb-6 rounded bg-gray  cursor-pointer leading-loose">
          <div className="text-lg font-semibold">Unlimited</div>
          <div className="text-base">99.99€</div>
          <div className="text-xs">mp3, wav</div>
        </div>
      </div>
      <div className="w-100 flex justify-between mt-6 cursor-pointer" onClick={() => setUseTermsDisplay()}>
        <span className="text-lg font-semibold">Usage Terms</span>
        <img id="dialogChevron" src="./static/chevron.svg" width="18px"></img>
      </div>
    </dialog>
  )
}

export default Dialog
